import { useParams } from 'react-router-dom';
import './EventoCriado.css';

export default function EventoCriado() {
  const { id } = useParams();
  const link = `${window.location.origin}/event/${id}`;

  const copiar = async () => {
    try {
      await navigator.clipboard.writeText(link);
      alert('Link copiado!');
    } catch {
      alert('Erro ao copiar link');
    }
  };

  return (
    <div className="created-container">
      <div className="created-card">
        <h1>âœ… Evento criado com sucesso!</h1>
        <p>Compartilhe este link com seus amigos para votarem:</p>
        <div className="link-box">
          <input value={link} readOnly />
          <button onClick={copiar}>Copiar Link</button>
        </div>
      </div>
    </div>
  );
}
