import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { api } from '../utils/api';
import QuestionForm from '../components/QuestionForm';
import { v4 as uuidv4 } from 'uuid';
import './CreateEvent.css';

export default function CreateEvent() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [questions, setQuestions] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  const addQuestion = () => {
    setQuestions([
      ...questions,
      { id: uuidv4(), text: '', multi: false, options: [''] }
    ]);
  };

  const removeQuestion = () => {
    if (questions.length > 0) {
      const newQuestions = [...questions];
      newQuestions.pop();
      setQuestions(newQuestions);
    }
  };

  const handleQuestionChange = (index, updated) => {
    const newQuestions = [...questions];
    newQuestions[index] = updated;
    setQuestions(newQuestions);
  };

  const createEvent = async () => {
    if (!title.trim()) {
      alert("Por favor, adicione um t√≠tulo para o evento.");
      return;
    }

    if (questions.length === 0) {
      alert("Adicione pelo menos uma pergunta ao evento.");
      return;
    }

    setIsLoading(true);

    try {
      const formattedQuestions = questions.map(q => ({
        id: q.id,
        text: q.text,
        multi: q.multi,
        options: q.options.map((opt, i) => ({
          _id: String(i),
          text: opt
        }))
      }));

      const res = await api.post('/events', {
        title,
        description,
        questions: formattedQuestions
      });

      navigate(`/evento-criado/${res.data.eventId}`);
    } catch (error) {
      console.error("Erro ao criar evento:", error);
      alert("Ocorreu um erro ao criar o evento. Tente novamente.");
    } finally {
      setIsLoading(false);
    }
  };

  const canCreateEvent = title.trim() && questions.length > 0;

  return (
    <div className="page-container">
      <div className="create-event">
        <div className="header-section">
          <h1>Criar Evento</h1>
          <p className="subtitle">Configure as perguntas que ajudar√£o seu grupo a decidir</p>
        </div>

        <div className="form-section">
          <div className="input-group">
            <label htmlFor="title">T√≠tulo do Evento</label>
            <input 
              id="title"
              className="title-description"
              placeholder="Ex: Encontro do fim de semana"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              maxLength={100}
            />
          </div>

          <div className="input-group">
            <label htmlFor="description">Descri√ß√£o (opcional)</label>
            <textarea 
              id="description"
              className="title-description textarea"
              placeholder="Adicione mais detalhes sobre o evento..."
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              maxLength={300}
              rows={3}
            />
          </div>
        </div>

        <div className="questions-section">
          <div className="questions-header">
            <h2>Perguntas</h2>
          </div>

          <div className="questions-list">
            {questions.map((q, i) => (
              <div key={q.id} className="question-wrapper">
                <div className="question-number">{i + 1}</div>
                <QuestionForm 
                  question={q} 
                  index={i} 
                  onChange={handleQuestionChange} 
                />
              </div>
            ))}
          </div>

          {questions.length === 0 && (
            <div className="empty-state">
              <div className="empty-icon">üìù</div>
              <p>Nenhuma pergunta adicionada ainda</p>
              <p className="empty-subtitle">Clique em "Adicionar Pergunta" para come√ßar</p>
            </div>
          )}
        </div>

        <div className="form-buttons">
          <div className="question-buttons">
            <button 
              type="button" 
              className="btn-secondary" 
              onClick={addQuestion}
            >
              ‚ûï Adicionar Pergunta
            </button>

            {questions.length > 0 && (
              <button 
                type="button" 
                className="btn-secondary btn-remove" 
                onClick={removeQuestion}
              >
                üóëÔ∏è Remover √öltima
              </button>
            )}
          </div>

          <button 
            type="button"
            className={`btn-primary ${!canCreateEvent ? 'btn-disabled' : ''}`}
            onClick={createEvent}
            disabled={!canCreateEvent || isLoading}
          >
            {isLoading ? (
              <>
                <span className="loading-spinner"></span>
                Criando...
              </>
            ) : (
              <>üöÄ Criar Evento</>
            )}
          </button>
        </div>
      </div>
    </div>
  );
}
